To: _left, constant: paddingLeft).isActive = true		}			if let _bottom = bottom {			self.bottomAnchor.constraint(equalTo: _bottom, constant: -paddingBottom).isActive = true		}			if let _right = right {			self.rightAnchor.constraint(equalTo: _right, constant: -paddingRight).isActive = true		}			if let _width = width {			self.widthAnchor.constraint(equalToConstant: _width).isActive = true		}			if let _height = height {			self.heightAnchor.constraint(equalToConstant: _height).isActive = true		}	}		func full(of view: UIView) {		self.anchor(top: view.topAnchor, left: view.leftAnchor, bottom: view.bottomAnchor, right: view.rightAnchor)	}			func anchor(top: NSLayoutYAxisAnchor?, left: NSLayoutXAxisAnchor?, bottom: NSLayoutYAxisAnchor?, right: NSLayoutXAxisAnchor?, width: NSLayoutDimension? = nil, height: NSLayoutDimension? = nil,  paddingTop: CGFloat = 0, paddingLeft: CGFloat = 0, paddingBottom: CGFloat = 0, paddingRight: CGFloat = 0, widthRatio: CGFloat = 1, heightRatio: CGFloat = 1) {			translatesAutoresizingMaskIntoConstraints = false			if let _top = top {			self.topAnchor.constraint(equalTo: _top, constant: paddingTop).isActive = true		}			if let _left = left {			self.leftAnchor.constraint(equalTo: _left, constant: paddingLeft).isActive = true		}			if let _bottom = bottom {			self.bottomAnchor.constraint(equalTo: _bottom, constant: -paddingBottom).isActive = true		}			if let _right = right {			self.rightAnchor.constraint(equalTo: _right, constant: -paddingRight).isActive = true		}			if let _width = width {			self.widthAnchor.constraint(equalTo: _width, multiplier: widthRatio).isActive = true		}			if let _height = height {			self.heightAnchor.constraint(equalTo: _height, multiplier: heightRatio).isActive = true		}	}}